import React,{useEffect, useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
    root: {
      textAlign: "center",
      padding: "2.5%"
    },
    gcard: {
    },
    table: {
        minWidth: 650,
    },
    tablecard: {
        maxWidth: "1200px",
        margin: "20px auto"
    }
  })

const Home = () => {
    const [gcase,setGdata] = useState({})
    const [ccase,setCdata] = useState([])
    const classes = useStyles();
       
    useEffect(() => {
        fetch("https://api.covid19api.com/summary")
        .then(response => response.json())
        .then(result => {
            setGdata(result.Global)
            setCdata(result.Countries)
        })
        .catch(error => console.log('error', error));
    },[])
    return(
        <div>
            <Card className={classes.root}>
                <Typography variant="h4" >Global Cases</Typography>
                <CardContent className={classes.gcard}>
                    <h4>Active Cases</h4>    
                    <div>{JSON.stringify(gcase.TotalConfirmed - gcase.TotalDeaths - gcase.TotalRecovered)}</div>
                </CardContent>
                <CardContent className={classes.gcard}>
                    <h4>Total Cases</h4>    
                    <div>{JSON.stringify(gcase.TotalConfirmed)}</div>
                </CardContent>
                <CardContent className={classes.gcard}>
                    <h4>Total Deaths</h4>    
                    <div>{JSON.stringify(gcase.TotalDeaths)}</div>
                </CardContent>
            </Card>
            <Card className={classes.tablecard}>
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table" stickyHeader="true">
                        <TableHead>
                        <TableRow>
                            <TableCell>Country</TableCell>
                            <TableCell align="centre">Active&nbsp;Cases</TableCell>
                            <TableCell align="centre">Total&nbsp;Cases</TableCell>
                            <TableCell align="centre">Recovered</TableCell>
                            <TableCell align="centre">Deaths</TableCell>
                        </TableRow>
                        </TableHead>
                        <TableBody>
                        {ccase.map((row) => (
                            <TableRow key={row.CountryCode}>
                            <TableCell component="th" scope="row">
                                {row.Country}
                            </TableCell>
                            <TableCell align="left">{row.Slug}</TableCell>
                            <TableCell align="left">{row.TotalConfirmed}</TableCell>
                            <TableCell align="left">{row.TotalRecovered}</TableCell>
                            <TableCell align="left">{row.TotalDeaths}</TableCell>
                            </TableRow>
                        ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Card>
        </div>
    )
}

export default Home
